package com.java5;

import java.util.ArrayList;

public class ForeachCollection {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		ArrayList<Integer> al=new ArrayList<Integer>();
		al.add(10);
		al.add(20);
		al.add(30);
		for(int n:al) {
			System.out.println(n);
		}
			

	}

}
